import react, { useEffect, useState, Component } from 'react';
import LeftContainer from './components/LeftContainer';
import RightContainer from './components/RightConatainer';
import Axios from 'axios';

function App() {
  const [currentWeather, setCurrentWeather] = useState('');

  useEffect(() => {
    getCurrentWeather();
  })

  const getCurrentWeather = () => {
    fetch("http://api.openweathermap.org/data/2.5/weather?q=Budapest&units=metric&appid=6208d268fe1e8d844738ac488873be57")
      .then((response) => response.json())
      .then((data) => {
        setCurrentWeather(data.result);
        return (
          currentWeather
        )
      }
      )
  };

  return (
    <div style={mainStyle}>
      <LeftContainer className='flex' currentWeather={currentWeather} />
      <RightContainer />
    </div >
  )
};

const mainStyle = {
  top: '0px',
  bottom: '0px',
  right: '0px',
  left: '0px',
  position: 'absolute',
  background: 'linear-gradient( #419AF2, #4FA9F2, #5EB9F1, #6CC8F1)',
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'space-around',
};

export default App;
