import SearchBar from "./SearchBar";
import BoxMain from "./BoxMain";
import BoxDown from "./BoxDown";

const LeftContainer = ({ currentWeather }) => {


    if (currentWeather.name || currentWeather.main.temp || currentWeather.weather.description === undefined) {
        return (
            <div style={leftContainerStyle}>
                <SearchBar />
                <BoxMain
                    name={'loading'}
                    temp={'loading'}
                    description={'loading'}
                />
                <BoxDown />
            </div>
        )
    } else {
        <div style={leftContainerStyle}>
            <SearchBar />
            <BoxMain
                name={currentWeather.name}
                temp={currentWeather.main.temp}
                description={currentWeather.weather.description}
            />
            <BoxDown />
        </div>
    }


};

const leftContainerStyle = {
    display: 'flex',
    flexDirection: 'column',
    width: '40%',
    height: '90%',
    alignItems: 'center',
    justifyContent: 'space-between',
};

export default LeftContainer;
