import react, { useEffect, useState, Component } from 'react';
import LeftContainer from './components/LeftContainer';
import RightContainer from './components/RightConatainer';
import Axios from 'axios';
import axios from 'axios';

function App() {
  const [currentWeather, setCurrentWeather] = useState({});
  //const isLoaded = false;

  useEffect(() => {
    getCurrentWeather();
  })

  const getCurrentWeather = async () => {
    await axios.get("http://api.openweathermap.org/data/2.5/weather?q=Budapest&units=metric&appid=051c320cd472d61c299358a0c88cb746")
      .then((response) => {
        setCurrentWeather(response.data);
        console.log(response.data);
        return (
          currentWeather
        )
      }
      )
  };

  return (
    <div style={mainStyle}>
      {(typeof currentWeather.weather != undefined) ? (< LeftContainer className='flex' currentWeather={currentWeather} />) : ('')}
      <RightContainer />
    </div >
  )
};

const mainStyle = {
  top: '0px',
  bottom: '0px',
  right: '0px',
  left: '0px',
  position: 'absolute',
  background: 'linear-gradient( #419AF2, #4FA9F2, #5EB9F1, #6CC8F1)',
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'space-around',
};

export default App;
