const BoxMain = (props) => {
    const img = "";
    const temp = props.temp;

    if ((props.id).match(/801/)) {
        img = "weatherIcons/few_clouds.png"
    }
    if ((props.id).match(/802/ || /803/ || /804/)) {
        img = "weatherIcons/clouds.png"
    }
    if ((props.id).match(/800/)) {
        img = "weatherIcons/clear.png"
    }
    if ((props.id).match(/^7/)) {
        img = "weatherIcons/fog.png"
    }
    if ((props.id).match(/^6/)) {
        img = "weatherIcons/snow.png"
    }
    if ((props.id).match(/(^5)|(^3)/)) {
        img = "weatherIcons/rain.png"
    }
    if ((props.id).match(/^2/)) {
        img = "weatherIcons/storm.png"
    }

    return (
        <div style={boxLeftStyle}>
            <div style={frameStyle}>

                <img style={iconStyle} src={img} />
            </div>
            <div style={infoStyle}>
                <div style={{ fontSize: '30px', padding: 0, margin: 0 }}>{props.name}</div>
                <div style={{ fontSize: '90px', fontWeight: '500', padding: 0, margin: 0 }}>{Math.round(temp)} Â°C</div>
                <div style={{ fontSize: '30px', padding: 0, margin: 0 }}>{props.description}</div>
            </div>


        </div >
    )
    //return (<div style={boxLeftStyle}></div>);
}

const boxLeftStyle = {
    margin: 0,
    padding: 0,
    width: '100%',
    height: '44%',
    background: 'rgba(255, 255, 255, 0.8)',
    borderRadius: '25px',
    boxShadow: '5px 5px 10px 5px rgba(0,0,0,0.1)',
    display: 'flex',
    flexDirection: 'row',
};

const infoStyle = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-around',
    alignItems: 'center',
    padding: '40px',
    paddingLeft: '20px'

};

const iconStyle = {
    width: '300px',
    height: '300px'
};

const frameStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
}




export default BoxMain;
