import react, { useEffect, useState, Component } from 'react';
import LeftContainer from './components/LeftContainer';
import RightContainer from './components/RightConatainer';
import Axios from 'axios';
import axios from 'axios';
import SearchBar from "./components/SearchBar";
import BoxMain from "./components/BoxMain";
import BoxDown from "./components/BoxDown";

function App() {
  const [currentWeather, setCurrentWeather] = useState({});
  const [cityName, setCityName] = useState('');
  //const isLoaded = false;


  const searchCity = async (text) => {
    //text.lowerCase();
    console.log(text);
    //const data = await axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${text}&units=metric&appid=798f24f0a2d52c4d05241da7f603bb1e`)
    await axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${text}&units=metric&appid=798f24f0a2d52c4d05241da7f603bb1e`)
      .then((response) => {
        setCurrentWeather(response.data);
        return (
          currentWeather
        )
      })
      .catch((error) => {
        if (error.request) {
          alert("There's no such city in the database.");
        }
      })
    return (
      currentWeather
    )
  };

  /*useEffect(() => {
    getCurrentWeather();
  }, []);*/

  const getCurrentWeather = async () => {

  };
  return (
    <div>
      {(typeof currentWeather.main != "undefined") ?
        (
          <div style={mainStyle}>
            <div style={leftContainerStyle}>
              <SearchBar searchCity={searchCity} />
              {(typeof currentWeather.main != "undefined") ?
                (<BoxMain
                  name={currentWeather.name}
                  temp={currentWeather.main.temp}
                  description={currentWeather.weather[0].description}
                  id={currentWeather.weather[0].id}
                  sunrise={currentWeather.sys.sunrise}
                  sunset={currentWeather.sys.sunset}
                  timezone={currentWeather.timezone}
                />) : (<div style={placeholderStyle}> Please type in a city</div>)}
              {(typeof currentWeather.main != "undefined") ?
                (<BoxDown
                  feels={currentWeather.main.feels_like}
                  wind={currentWeather.wind.speed}
                  humidity={currentWeather.main.humidity}
                  sunrise={currentWeather.sys.sunrise}
                  sunset={currentWeather.sys.sunset}
                />) : (<div style={placeholderStyle}></div>)}
            </div>
            <RightContainer />
          </div >
        ) :
        (
          <div style={blankStyle}>
            <p style={{ fontSize: "45px", fontWeight: '700', color: '#FEFFED' }}>Hello, please search for your city.</p>
            <SearchBar searchCity={searchCity} />
          </div>
        )}
    </div>
  )
};

const mainStyle = {
  top: '0px',
  bottom: '0px',
  right: '0px',
  left: '0px',
  position: 'absolute',
  background: 'linear-gradient( #419AF2, #4FA9F2, #5EB9F1, #6CC8F1)',
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'space-around',
};

const placeholderStyle = {
  margin: 0,
  padding: 0,
  width: '100%',
  height: '44%',
  background: 'rgba(255, 255, 255, 0.8)',
  borderRadius: '25px',
  boxShadow: '5px 5px 10px 5px rgba(0,0,0,0.1)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  flexDirection: 'row',
};

const leftContainerStyle = {
  display: 'flex',
  flexDirection: 'column',
  width: '40%',
  height: '90%',
  alignItems: 'center',
  justifyContent: 'space-between',
};

const blankStyle = {
  top: 0,
  right: 0,
  bottom: 0,
  left: 0,
  position: 'absolute',
  background: 'linear-gradient( #419AF2, #4FA9F2, #5EB9F1, #6CC8F1)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  flexDirection: 'column',
  paddingLeft: '25%',
  paddingRight: '25%',
};

export default App;
