import moment from "moment";

const BoxMain = (props) => {
    let img = "";
    const id = props.id;
    const temp = props.temp;
    const timezone = props.timezone;
    const localTime = (parseInt(moment.utc() + timezone));
    console.log(moment.utc().format("HH:mm"))
    console.log(moment((moment.utc())).add((parseInt(timezone / 3600)), 'hours'))
    console.log(moment.unix(parseInt(moment.utc() + timezone)).format())
    console.log(moment().format());

    if (/801/.test(id)) {
        ((moment.utc() + timezone) < moment.unix(props.sunrise).format() || (moment.unix().format() + timezone) > moment.unix(props.sunset).format()) ?
            (img = "few_clouds_d.png") : (img = "few_clouds_n.png")
    }
    if ((/802/.test(id)) || (/803/.test(id)) || (/804/.test(id))) {
        img = "clouds.png"
    }
    if (/800/.test(id)) {
        (moment.unix().format() < moment.unix(props.sunrise).format() || moment.unix().format() > moment.unix(props.sunset).format()) ?
            (img = "clear_n.png") : (img = "clear_d.png")

    }
    if (/^7/.test(id)) {
        img = "fog.png"
    }
    if (/^6/.test(id)) {
        img = "snow.png"
    }
    if ((/^5/.test(id)) || (/^3/.test(id))) {
        img = "rain.png"
    }
    if (/^2/.test(id)) {
        img = "storm.png"
    }

    return (
        <div style={boxLeftStyle}>
            <div style={frameStyle}>
                <img style={iconStyle} src={`weatherIcons/${img}`} />
            </div>
            <div style={infoStyle}>
                <div style={{ fontSize: '30px', padding: 0, margin: 0 }}>{props.name}</div>
                <div style={{ fontSize: '90px', fontWeight: '500', padding: 0, margin: 0 }}>{Math.round(temp)} Â°C</div>
                <div style={{ fontSize: '30px', padding: 0, margin: 0 }}>{props.description}</div>
            </div>


        </div >
    )
    //return (<div style={boxLeftStyle}></div>);
}

const boxLeftStyle = {
    margin: 0,
    padding: 0,
    width: '100%',
    height: '44%',
    background: 'rgba(255, 255, 255, 0.8)',
    borderRadius: '25px',
    boxShadow: '5px 5px 10px 5px rgba(0,0,0,0.1)',
    display: 'flex',
    flexDirection: 'row',
};

const infoStyle = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-around',
    alignItems: 'center',
    padding: '40px',
    paddingLeft: '20px'

};

const iconStyle = {
    width: '300px',
    height: '300px'
};

const frameStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
}




export default BoxMain;
