        imgPath = 'weatherIcons/clear_d.png';
    };

    return (
        <div style={boxLeftStyle}>
            <div>
                <img src={imgPath} />
            </div>
            <div style={infoStyle}>
                <p style={{ padding: '15px' }}>{props.name}</p>
