import react, { useState } from 'react';
import LeftContainer from './components/LeftContainer';
import RightContainer from './components/RightConatainer';
import axios from 'axios';

function App() {
  const getCurrentWeather = () => {
    Axios.get("http://api.openweathermap.org/data/2.5/weather?q=Budapest&lang=hu&appid=798f24f0a2d52c4d05241da7f603bb1e")
      .then((repsponse) => {
        console.log();
      } 
    )
  };

  const [cities, setCities] = useState([
    {
      id: 1,
      city: 'Budapest',
      temperature: 21,
      unit: '°C',
    }
  ])

  return (
    <div style={mainStyle}>
      <LeftContainer className='flex' data={cities} />
      <RightContainer />
    </div >
  )
};

const mainStyle = {
  top: '0px',
  bottom: '0px',
  right: '0px',
  left: '0px',
  position: 'absolute',
  background: 'linear-gradient( #419AF2, #4FA9F2, #5EB9F1, #6CC8F1)',
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'space-around',
};

export default App;
